insert into messages (id, activated, body, community_id, frequence, messageType, position, publishTimeMillis, title) values (2, true, 'The Wanted would love to match the drama and musicality of the Take That shows on their upcoming tour. The boys start a 10 date US Tour on Jan 17th and then head back to the UK for their 1st show on February 15th at the Capital FM Arena in Nottingham.', 5, 'DAILY', 'POPUP',2, 1315686788000, 'The Wanted would love to match the drama and musicality of the Take That shows on their upcoming tour. The boys start a 10 date US Tour on Jan 17th and then head back to the UK for their 1st show on February 15th at the Capital FM Arena in Nottingham.');
